import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import moment from 'moment';
import styled from 'styled-components';
import { firebase, storage } from '../firebase';
import YouTube from 'react-youtube';

export default function Portfolio() {
    const [title, setTitle] = useState('');
    const [writer, setWriter] = useState('');
    const [description, setDescription] = useState('');
    const [videoId, setVideoId] = useState('');

    const allInputs = { imgUrl: '' };
    const [image, setImage] = useState('');
    const [imageAsFile, setImageAsFile] = useState('');
    const [imageAsUrl, setImageAsUrl] = useState(allInputs);
    const [rendering, setRendering] = useState([]);

    let progress = 0;

    useEffect(() => {
        console.log(image);
        const unsubscribe = firebase
            .firestore()
            .collection('tasks')
            .onSnapshot((snapshot) => {
                const newRendering = snapshot.docs.map((doc) => {
                    return {
                        id: doc.id,
                        ...doc.data(),
                    };
                });
                console.log(newRendering);
                setRendering(newRendering);
            });

        return () => unsubscribe();
    }, []);

    const handleFireBaseUpload = (e) => {
        e.preventDefault();
        console.log('start of upload');
        if (imageAsFile === '') {
            console.log(
                `not an image, the image file is ${typeof imageAsFile}`
            );
        }
        const uploadTask = storage
            .ref(`images/${imageAsFile.name}`)
            .put(imageAsFile);

        uploadTask.on(
            'state_changed',
            (snapshot) => {
                progress =
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                console.log('Upload is ' + progress + '% done');
                switch (snapshot.state) {
                    case firebase.storage.TaskState.PAUSED:
                        console.log('Upload is paused');
                        break;
                    case firebase.storage.TaskState.RUNNING:
                        console.log('Upload is running');
                        break;
                }
            },
            (err) => {
                console.log(err);
            },
            () => {
                storage
                    .ref('images')
                    .child(imageAsFile.name)
                    .getDownloadURL()
                    .then((fireBaseUrl) => {
                        setImage(fireBaseUrl);
                        setImageAsUrl((prevObject) => ({
                            ...prevObject,
                            imgUrl: fireBaseUrl,
                        }));
                        console.log(fireBaseUrl);
                    });
            }
        );

        firebase
            .firestore()
            .collection('tasks')
            .add({
                title,
                writer,
                description,
                videoId,
                image,
                moment: moment().format('YYYY-MM-DD_h:mm:ss'),
            });
        // }
    };

    const handleImageAsFile = (e) => {
        const image = e.target.files[0];
        setImageAsFile(image);
    };

    const addTask = () => {
        // if (
        //     portfolioInfo.title ||
        //     portfolioInfo.writer ||
        //     portfolioInfo.description !== ''
        // ) {
    };

    const handleDelete = (id) => {
        firebase.firestore().collection('tasks').doc(id).delete();
    };

    const opts = {
        height: '200',
        width: '340',
        playerVars: {
            // https://developers.google.com/youtube/player_parameters
            autoplay: 0,
        },
    };

    const _onReady = (event) => {
        // access to player in all event handlers via event.target
        event.target.pauseVideo();
    };

    return (
        <div>
            <h1>추가하기</h1>
            <button>
                <Link to='/add_portfolio'>추가하기</Link>
            </button>
            <h1>약력</h1>
            <StyledPre>
                {rendering
                    .sort((a, b) => {
                        return a.goal - b.goal;
                    })
                    .map((r, i) => {
                        return (
                            <StyledDiv1 key={r.id}>
                                <img
                                    width='100'
                                    height='100'
                                    src={image}
                                    alt='image tag'
                                />
                                <StyledDiv2>
                                    <h3 style={{ margin: '0 10px' }}>
                                        {r.title}
                                    </h3>
                                    <h3 style={{ margin: '0 10px' }}>
                                        {r.writer}
                                    </h3>
                                    <h3 style={{ margin: '0 10px' }}>
                                        {r.description}
                                    </h3>
                                </StyledDiv2>
                                <h6>{r.moment}</h6>
                                <button onClick={() => handleDelete(r.id)}>
                                    Delete
                                </button>
                                <YouTube
                                    videoId={r.videoId}
                                    opts={opts}
                                    onReady={_onReady}
                                />
                            </StyledDiv1>
                        );
                    })}
                {/* <video src={youtube} width='300' /> */}
                <h1 style={{ textAlign: 'center' }}>Progress: {progress}</h1>
            </StyledPre>
            <progress value={progress} max='100' />
            <StyledSummaryBorder>
                <form onSubmit={handleFireBaseUpload}>
                    <h1>Add Portfolio</h1>
                    <h3>제목</h3>
                    <input onChange={(e) => setTitle(e.target.value)} />
                    <h3>작성자</h3>
                    <input onChange={(e) => setWriter(e.target.value)} />
                    <h3>설명</h3>
                    <StyledTextArea
                        onChange={(e) => setDescription(e.target.value)}
                    />
                    <h3>유튜브 인풋</h3>
                    <input onChange={(e) => setVideoId(e.target.value)} />
                    <h3>첨부파일</h3>
                    <StyledInput type='file' onChange={handleImageAsFile} />
                    <StyledButton type='submit'>제출</StyledButton>
                </form>
            </StyledSummaryBorder>
        </div>
    );
}

const StyledPre = styled.pre`
    text-align: left;
`;

const StyledDiv1 = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    margin: 20px;
`;

const StyledDiv2 = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
`;

const StyledSummaryBorder = styled.div`
    display: flex;
    flex-direction: column;
    border: 2px solid black;
    padding: 10px;
    margin: 10px;
    border-radius: 8px; ;
`;

const StyledInput = styled.input`
    width: 200px;
    border: 2px solid red;
`;

const StyledTextArea = styled.textarea`
    width: 200px;
    border: 2px solid red;
`;

const StyledButton = styled.button`
    width: 200px;
`;
