import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import moment from 'moment';
import styled from 'styled-components';
import { firebase } from '../firebase';

export default function Portfolio() {
    const [goal, setGoal] = useState('');
    const [rendering, setRendering] = useState([]);

    const addTask = () => {
        if (goal !== '') {
            firebase
                .firestore()
                .collection('tasks')
                .add({
                    goal: goal,
                    moment: moment().format('YYYY-MM-DD, h:mm:ss'),
                })
                .then(() => {
                    setGoal('');
                });
        }
    };

    useEffect(() => {
        const unsubscribe = firebase
            .firestore()
            .collection('tasks')
            .onSnapshot((snapshot) => {
                const newRendering = snapshot.docs.map((doc) => {
                    return {
                        id: doc.id,
                        ...doc.data(),
                    };
                });
                console.log(newRendering);
                setRendering(newRendering);
            });

        return () => unsubscribe();
    }, []);

    return (
        <div>
            <h1>추가하기</h1>
            <button>
                <Link to='/add_portfolio'>추가하기</Link>
            </button>
            <h1>약력</h1>
            <StyledPre>
                {rendering
                    .sort((a, b) => {
                        return a.goal - b.goal;
                    })
                    .map((r, i) => {
                        return (
                            <StyledDiv1 key={i}>
                                <h3 style={{ margin: '0 10px' }}>{r.goal}</h3>
                                <h6>{r.moment}</h6>
                            </StyledDiv1>
                        );
                    })}
            </StyledPre>
            <StyledSummaryBorder>
                <StyledTextArea onChange={(e) => setGoal(e.target.value)} />
                <StyledButton onClick={addTask}>Setting Goal</StyledButton>
                <StyledInput type='file' />
            </StyledSummaryBorder>
        </div>
    );
}

const StyledPre = styled.pre`
    text-align: center;
`;

const StyledDiv1 = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const StyledSummaryBorder = styled.div`
    position: relative;
    display: flex;
    flex-direction: column;
    border: 2px solid black;
    border-radius: 8px;
`;

const StyledInput = styled.input`
    width: 200px;
    border: 2px solid red;
    position: absolute;
    left: 50%;
`;

const StyledTextArea = styled.textarea`
    width: 200px;
    border: 2px solid red;
    position: absolute;
    left: 50%;
`;
const StyledButton = styled.button`
    position: absolute;
    left: 50%;
    width: 200px;
`;
