import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import moment from 'moment';
import styled from 'styled-components';
import { firebase } from '../firebase';

export default function Portfolio() {
    const [title, setTitle] = useState('');
    const [writer, setWriter] = useState('');
    const [description, setDescription] = useState('');
    const [portfolioInfo, setPortfolioInfo] = useState({
        title: title,
        writer: writer,
        description: description,
    });
    const [rendering, setRendering] = useState([]);

    const addTask = () => {
        // if (
        //     portfolioInfo.title ||
        //     portfolioInfo.writer ||
        //     portfolioInfo.description !== ''
        // ) {

        setTitle(e.target.value);
        setWriter(e.target.value);
        setDescription(e.target.value);
        firebase
            .firestore()
            .collection('tasks')
            .add({
                portfolioInfo: portfolioInfo,
                moment: moment().format('YYYY-MM-DD, h:mm:ss'),
            });
        // }
    };

    const handleChange = (e) => {};

    useEffect(() => {
        const unsubscribe = firebase
            .firestore()
            .collection('tasks')
            .onSnapshot((snapshot) => {
                const newRendering = snapshot.docs.map((doc) => {
                    return {
                        id: doc.id,
                        ...doc.data(),
                    };
                });
                console.log(newRendering);
                setRendering(newRendering);
            });

        return () => unsubscribe();
    }, []);

    const handleDelete = (id) => {
        firebase.firestore().collection('tasks').doc(id).delete();
    };

    return (
        <div>
            <h1>추가하기</h1>
            <button>
                <Link to='/add_portfolio'>추가하기</Link>
            </button>
            <h1>약력</h1>
            <StyledPre>
                {rendering
                    .sort((a, b) => {
                        return a.goal - b.goal;
                    })
                    .map((r, i) => {
                        return (
                            <StyledDiv1 key={r.id}>
                                <h3 style={{ margin: '0 10px' }}>{r.goal}</h3>
                                <h6>{r.moment}</h6>
                                <button onClick={() => handleDelete(r.id)}>
                                    Delete
                                </button>
                            </StyledDiv1>
                        );
                    })}
            </StyledPre>
            <StyledSummaryBorder>
                <h1>Add Portfolio</h1>
                <h3>제목</h3>
                <input onChange={(e) => setTitle(e.target.value)} />
                <h3>작성자</h3>
                <input onChange={(e) => setWriter(e.target.value)} />
                <h3>설명</h3>
                <StyledTextArea
                    onChange={(e) => setDescription(e.target.value)}
                />
                <h3>첨부파일</h3>
                <StyledInput type='file' />
                <StyledButton onClick={addTask}>Submit</StyledButton>
            </StyledSummaryBorder>
        </div>
    );
}

const StyledPre = styled.pre`
    text-align: left;
`;

const StyledDiv1 = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const StyledSummaryBorder = styled.div`
    display: flex;
    flex-direction: column;
    border: 2px solid black;
    padding: 10px;
    margin: 10px;
    border-radius: 8px; ;
`;

const StyledInput = styled.input`
    width: 200px;
    border: 2px solid red;
`;

const StyledTextArea = styled.textarea`
    width: 200px;
    border: 2px solid red;
`;

const StyledButton = styled.button`
    width: 200px;
`;
