import React, { useState } from 'react';
import YTSearch from 'youtube-api-search';
import _ from 'lodash';

const API_KEY = 'AIzaSyDWQwK8RrrQKM4HCuEyO-nkjiZB0MykvS8';

export default function YoutubeChannel() {
    const [data, setData] = useState([]);
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [term, setTerm] = useState('');

    const videoSearch = (term) => {
        YTSearch({ key: API_KEY, term: term }, (data) => {
            this.setState({
                data: data,
                selectedVideo: data[0],
                term: term,
            });
        });
    };

    const onVideoSelect = (selectedVideo) => {
        this.setState({ selectedVideo });
    };

    return (
        <div className='App'>
            <header className=' pt-3'>
                <div className='navbar'>
                    <div className='container justify-content-between'>
                        <a
                            href='http://sumim-project.surge.sh/'
                            className='navbar-brand text-white'>
                            <h2>Youtube List</h2>
                        </a>
                        <VideoSearch onCreate={this.videoSearch} />
                    </div>
                </div>
            </header>
            <main className='container pt-3'>
                <VideoList
                    videos={this.state.data}
                    onVideoSelect={(selectedVideo) =>
                        this.onVideoSelect(selectedVideo)
                    }
                />
                <VideoDetails video={this.state.selectedVideo} />
            </main>
        </div>
    );
}
