import React, { useState, useEffect } from 'react';
import moment from 'moment';
import styled from 'styled-components';
import { firebase, storage } from '../firebase';
import YouTube from 'react-youtube';
import ReactCrop from '../utils/Crop';

export default function MyPortfolio() {
    const [title, setTitle] = useState('');
    const [writer, setWriter] = useState('');
    const [description, setDescription] = useState('');
    const [videoId, setVideoId] = useState('');

    const allInputs = { imgUrl: '' };
    const [progress, setProgress] = useState(0);
    const [image, setImage] = useState('');
    const [imageAsFile, setImageAsFile] = useState('');
    const [imageAsUrl, setImageAsUrl] = useState(allInputs);
    const [rendering, setRendering] = useState([]);

    const [count, setCount] = useState(0);

    const [error, setError] = useState('');

    useEffect(() => {
        console.log(image);
        const unsubscribe = firebase
            .firestore()
            .collection('tasks')
            .orderBy('moment', 'desc')
            .onSnapshot((snapshot) => {
                const newRendering = snapshot.docs.map((doc) => {
                    return {
                        id: doc.id,
                        ...doc.data(),
                    };
                });
                console.log(newRendering);
                setRendering(newRendering);
            });

        return () => unsubscribe();
    }, []);

    const appliedHandleFireBaseUpload = (e) => {
        if (
            title === '' ||
            writer === '' ||
            description === '' ||
            videoId === '' ||
            imageAsFile === ''
        ) {
            console.log('Please Input something');
            setError('Please Input something');
            setCount((prevCount) => prevCount + 1);
            e.preventDefault();
        } else {
            handleFireBaseUpload(e);
        }
    };

    const handleFireBaseUpload = (e) => {
        e.preventDefault();
        console.log('start of upload');
        if (imageAsFile === '') {
            console.log(
                `not an image, the image file is ${typeof imageAsFile}`
            );
        }
        const uploadTask = storage
            .ref(`images/${imageAsFile.name}`)
            .put(imageAsFile);

        uploadTask.on(
            'state_changed',
            (snapshot) => {
                let progress =
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                if (progress >= 100) {
                    progress = 0;
                }
                console.log('Upload is ' + progress + '% done');
                setProgress(progress);
                switch (snapshot.state) {
                    case firebase.storage.TaskState.PAUSED:
                        console.log('Upload is paused');
                        break;
                    case firebase.storage.TaskState.RUNNING:
                        console.log('Upload is running');
                        break;
                }
            },
            (err) => {
                console.log(err);
            },
            () => {
                storage
                    .ref('images')
                    .child(imageAsFile.name)
                    .getDownloadURL()
                    .then((fireBaseUrl) => {
                        firebase
                            .firestore()
                            .collection('tasks')
                            .add({
                                title,
                                writer,
                                description,
                                videoId,
                                moment: moment().format('YYYY-MM-DD_h:mm:ss'),
                                image: fireBaseUrl,
                            });
                        setImageAsUrl((prevObject) => ({
                            ...prevObject,
                            imgUrl: fireBaseUrl,
                        }));
                        console.log(fireBaseUrl);
                    });
            }
        );
        setTitle('');
        setWriter('');
        setDescription('');
    };

    const handleImageAsFile = (e) => {
        const image = e.target.files[0];
        setImageAsFile(image);
    };

    const addTask = () => {
        // if (
        //     portfolioInfo.title ||
        //     portfolioInfo.writer ||
        //     portfolioInfo.description !== ''
        // ) {
    };

    const handleDelete = (id) => {
        firebase.firestore().collection('tasks').doc(id).delete();
    };

    const opts = {
        width: '350',
        height: '200',
        playerVars: {
            // https://developers.google.com/youtube/player_parameters
            autoplay: 0,
        },
    };

    const _onReady = (event) => {
        // access to player in all event handlers via event.target
        event.target.pauseVideo();
    };

    return (
        <StyledDiv3>
            <h1>SHOW ME WHAT YOU GOT!!</h1>
            <StyledProgress value={progress} max='100' />
            <StyledGrid1>
                {rendering.map((r, i) => {
                    return (
                        <StyledDiv2 key={r.id}>
                            <StyledDiv4>
                                <h3>{r.writer}</h3>
                                <h6>{r.moment}</h6>
                            </StyledDiv4>
                            <StyledDiv4>
                                <h3>{r.title}</h3>
                                <h3>{r.description}</h3>
                                <YouTube
                                    videoId={r.videoId}
                                    opts={opts}
                                    onReady={_onReady}
                                />
                                <div>
                                    <StyledImg
                                        width='350'
                                        height='200'
                                        src={r.image}
                                        alt='image tag'
                                    />
                                </div>
                            </StyledDiv4>
                            <button onClick={() => handleDelete(r.id)}>
                                Delete
                            </button>
                        </StyledDiv2>
                    );
                })}
            </StyledGrid1>
            <StyledSummaryBorder>
                <form onSubmit={appliedHandleFireBaseUpload}>
                    <h1>Add Portfolio</h1>
                    <h3>* 제목 *</h3>
                    <StyledInput onChange={(e) => setTitle(e.target.value)} />
                    <h3>* 참여자 *</h3>
                    <StyledInput onChange={(e) => setWriter(e.target.value)} />
                    <h3>* 간단설명 *</h3>
                    <StyledInput
                        onChange={(e) => setDescription(e.target.value)}
                    />
                    <h3>유튜브 인풋</h3>
                    <StyledInput onChange={(e) => setVideoId(e.target.value)} />
                    <h3>첨부파일</h3>
                    <ReactCrop />
                    <StyledFileLabel htmlFor='upload_file'>
                        파일 업로드
                    </StyledFileLabel>
                    <StyledFile
                        type='file'
                        id='upload_file'
                        onChange={handleImageAsFile}
                    />
                    <br />
                    <br />
