import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import moment from 'moment';
import styled from 'styled-components';
import { firebase } from '../firebase';

export default function Portfolio() {
    const [goal, setGoal] = useState('');
    const [rendering, setRendering] = useState([]);

    const addTask = () => {
        firebase
            .firestore()
            .collection('tasks')
            .add({
                goal: goal,
                moment: moment().format('YYYY-MM-DD, h:mm:ss'),
            })
            .then(() => {
                setGoal('');
            });
    };

    useEffect(() => {
        const unsubscribe = firebase
            .firestore()
            .collection('tasks')
            .onSnapshot((snapshot) => {
                const newRendering = snapshot.docs.map((doc) => {
                    return {
                        id: doc.id,
                        ...doc.data(),
                    };
                });
                console.log(newRendering);
                setRendering(newRendering);
            });

        return () => unsubscribe();
    }, []);

    return (
        <div>
            <h1>추가하기</h1>
            <button>
                <Link to='/add_portfolio'>추가하기</Link>
            </button>
            <h1>약력</h1>
            {rendering
                .sort((a, b) => {
                    return a.goal - b.goal;
                })
                .map((r, i) => {
                    return (
                        <StyledDiv1 key={i}>
                            <h3 style={{ margin: '0 10px' }}>{r.goal}</h3>
                            <h6>{r.moment}</h6>
                        </StyledDiv1>
                    );
                })}
            <StyledSummaryBorder>
                <input onChange={(e) => setGoal(e.target.value)} />
                <StyledButton onClick={addTask}>Setting Goal</StyledButton>
            </StyledSummaryBorder>
        </div>
    );
}

const StyledDiv1 = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const StyledButton = styled.button`
    margin: 0 35vw;
    width: 20vw;
`;

const StyledSummaryBorder = styled.div`
    display: flex;
    flex-direction: column;
    border: 2px solid black;
    padding: 10px;
    border-radius: 8px;
`;
