import moment from 'moment';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { firebase } from '../firebase';

export default function Portfolio() {
    const [goal, setGoal] = useState('');
    const [rendering, setRendering] = useState([]);

    const addTask = () => {
        firebase
            .firestore()
            .collection('tasks')
            .add({
                goal: goal,
            })
            .then(() => {
                setGoal('');
            });
    };

    useEffect(() => {
        const unsubscribe = firebase
            .firestore()
            .collection('tasks')
            .orderBy('tasks', 'desc')
            .onSnapshot((snapshot) => {
                const newRendering = snapshot.docs.map((doc) => {
                    return {
                        id: doc.id,
                        ...doc.data(),
                    };
                });
                console.log(newRendering);
                setRendering(newRendering);
            });

        return () => unsubscribe();
    }, [rendering]);

    return (
        <div>
            <h1>추가하기</h1>
            <button>
                <Link to='/add_portfolio'>추가하기</Link>
            </button>
            <h1>약력</h1>
            {rendering.map((r) => {
                return <p key={r.id}>{r.goal}</p>;
            })}
            <div className='summaryBorder'>
                목표: 추가하기에서 글써서 여기에 글쓰기
                <input onChange={(e) => setGoal(e.target.value)} />
                <button onClick={addTask}>Setting Goal</button>
            </div>
        </div>
    );
}
